mapper.update:
UPDATE product
SET is_deleted = 1
WHERE id = #{id}

UPDATE product
SET views = views + 1
WHERE id = #{id}

UPDATE product
SET wishlist_count = wishlist_count + 1
WHERE id = #{id}

mapper.select:
SELECT count(*)
FROM product
WHERE is_deleted = 0

    AND title LIKE CONCAT('%', #{keyword}, '%')


    AND category_id IN

        #{catId}



    AND price >= #{minPrice}


    AND price <= #{maxPrice}

SELECT 
FROM product p
LEFT JOIN product_category c ON p.category_id = c.id

    p.is_deleted = 0

        AND p.title LIKE CONCAT('%', #{keyword}, '%')


        AND p.category_id IN

            #{catId}



        AND p.price >= #{minPrice}


        AND p.price <= #{maxPrice}




        ORDER BY p.views DESC, p.id DESC


        ORDER BY p.wishlist_count DESC, p.id DESC


        ORDER BY p.created_at DESC, p.id DESC


LIMIT #{size} OFFSET #{offset}
